let b=new Date,c=new Date(b.getFullYear(),11,25);11==b.getMonth()&&b.getDate()>=25&&c.setFullYear(c.getFullYear()+1);let f=document.getElementById("days"),g=document.getElementById("hours"),h=document.getElementById("minutes"),i=document.getElementById("seconds");function updateClock(e){let t=Math.floor(e/86400);e-=86400*t;let a=Math.floor(e/3600)%24;e-=3600*a;let n=Math.floor(e/60)%60;e-=60*n;let o=Math.floor(e%60);f.innerHTML=padNumber(t),g.innerHTML=padNumber(a),h.innerHTML=padNumber(n),i.innerHTML=padNumber(o)}function padNumber(e){return e<10?"0"+e:e}setInterval(function(){var e=new Date;updateClock((c-e)/1e3)},1e3);